mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PROFILE_PIC = #{member_profile_pic}
WHERE MEMBER_SEQ = #{member_seq}

UPDATE TBL_MEMBER
SET MEMBER_PW = #{member_pw}
WHERE MEMBER_SEQ = #{member_seq}

UPDATE TBL_MEMBER
SET MEMBER_NICKNAME = #{member_nickname}
WHERE MEMBER_SEQ = #{member_seq}

UPDATE TBL_MEMBER
SET MEMBER_TEXT = #{member_text}
WHERE MEMBER_SEQ = #{member_seq}

UPDATE TBL_MEMBER
SET MEMBER_EMAIL_CHECKED = 1
WHERE MEMBER_SEQ = #{member_seq}
